import { Component } from "react";
import AppFilter from "../app-filter/app-filter";
import AppInfo from "../app-info/app-info";
import EmployeesAddForm from "../employees-add-form/employees-add-form";
import EmployeeList from "../employees-list/employees-list";
import SearchPanel from "../search-panel/search-panel";
import "./app.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [
        { name: "Don Carleone", salary: "1280", increase: true, id: 1 },
        { name: "Max Denny", salary: "2200", increase: false, id: 2 },
        { name: "Lucie Pete", salary: "3500", increase: false, id: 3 }
      ]
    }
  }

  onDeleteItem = (id) => {
    this.setState(({ data }) => {
      return {
        data: data.filter(item => item.id !== id)
      }
    })
  }

  addForm = (name, salary) => {
    let maxId = 3;
    const newItem = {
      name: name,
      salary: salary,
      increase: false,
      id: maxId + 1
    }
    this.setState((state) => {
      const newArr = [...state.data, newItem]
      return {
        newArr
      }
    })
  }



  render() {
    return (
      <div className="app">
        <AppInfo />
        <div className="search-panel">
          <SearchPanel />
          <AppFilter />
        </div>
        <EmployeeList
          data={this.state.data}
          onDelete={this.onDeleteItem} />
        <EmployeesAddForm addForm={this.addForm} />
      </div>
    )
  }
}

export default App
