import { Component } from "react";

import AppInfo from "../app-info/app-info";
import SearchPanel from "../search-panel/search-panel";
import AppFilter from "../app-filter/app-filter";
import EmployeesList from "../employees-list/employees-list";
import EmployeesAddForm from "../employees-add-form/employees-add-form";

import "./app.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [
        { name: "Clark Dilan", salary: 8200, increase: true, rise: true },
        { name: "Polly Moizo", salary: 4000, increase: false, rise: false },
        { name: "Sam Trace", salary: 2480, increase: true, rise: false },
      ],
    };
  }

  deleteItem = (id) => {
    this.setState(({ data }) => {
      return { data: data.filter((item, i) => i !== id) };
    });
  };

  addEmployee = (name, salary) => {
    if (name && salary) {
      const newObj = {
        name: name,
        salary: salary,
        increase: true,
        rise: false
      }
      this.setState(({ data }) => {
        return { data: [...data, newObj] }
      });
    }
  };

  onToggleIncrease = (id) => {
    this.setState(({data}) => {
      const index = data.findIndex((item,i) => i === id);
      console.log(index)
      const old = data[index];
      console.log(old)
    })
  }

  onToggleRise = (id) => {
    console.log(`Rise ${id}`)
  }

  render() {
    return (
      <div className="app">
        <AppInfo />
        <div className="search-panel">
          <SearchPanel />
          <AppFilter />
        </div>
        <EmployeesList data={this.state.data} onDelete={this.deleteItem}
          onToggleIncrease={this.onToggleIncrease}
          onToggleRise={this.onToggleRise} />
        <EmployeesAddForm addEmployee={this.addEmployee} />
      </div>
    );
  }
}

export default App;
