import './btn-group.css'

const AppFilter = (props) => {
    const buttonsData = [
        {label: 'All employees', name: 'all'},
        {label: 'Salary over 1000 $', name: 'moreThan1000'},
        {label: 'Rising', name: 'rise'}
    ]

    const buttons = buttonsData.map((item) => {
        const active = props.filter === item.name;
        const style = active ? 'btn-light' : 'btn-outline-light'
        return (
            <button type="button"
            onClick={props.onFilterSelect(item.name)}
                key={item.name}
                className={`btn ${style}`}>
                {item.label}
            </button>
        )
    })


       return (
        <div className="btn-group">
            {buttons}
        </div>
    )
}

export default AppFilter
