import { Component } from "react";

import AppInfo from "../app-info/app-info";
import SearchPanel from "../search-panel/search-panel";
import AppFilter from "../app-filter/app-filter";
import EmployeesList from "../employees-list/employees-list";
import EmployeesAddForm from "../employees-add-form/employees-add-form";

import "./app.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [
        { name: "Clark Dilan", salary: 8200, increase: true },
        { name: "Polly Moizo", salary: 4000, increase: false },
        { name: "Sam Trace", salary: 2480, increase: true },
      ],
    };
  }

  deleteItem = (id) => {
    this.setState(({ data }) => {
      return { data: data.filter((item, i) => i !== id) };
    });
  };

  handleSumbit = (e) => {
    e.preventDefault();
    console.log(this.state.data[0].name)
  }

  render() {
    return (
      <div className="app">
        <AppInfo />
        <div className="search-panel">
          <SearchPanel />
          <AppFilter />
        </div>
        <EmployeesList data={this.state.data} onDelete={this.deleteItem} />
        <EmployeesAddForm handleSumbit={this.handleSumbit}/>
      </div>
    );
  }
}

export default App;
