                    <input type="text"
                        onChange={this.onValueChange}
                        name="name"
                        className="form-control new-post-label"
                        placeholder="What's his name" />
                    <input type="number"
                        onChange={this.onValueChange}
                        name="salary"
                        className="form-control new-post-label"
                        placeholder="Salary?" />

                    <button type="submit"
                        onSubmit={onSubmitForm}
                        className="btn btn-outline-light">Add</button>
                </form>
            </div>

        )
    }
}

export default EmployeesAddForm
